// === CONFIGURACIÃ“N ===
// Lista de columnas exactas segÃºn tu tabla:
const columns = [
  "COUNTRY", "CITY", "ZONE", "METRIC",
  "L8W", "L7W", "L6W", "L5W", "L4W", "L3W", "L2W", "L1W", "L0W"
];

// Nombre de la tabla (usa mayÃºsculas y comillas dobles)
const tableName = "RAW_ORDERS";

// === OBTENER SQL DEL NODO ANTERIOR ===
let sql = $input.first().json.text

// === LIMPIEZA GENERAL ===
sql = sql.replace(/\r?\n/g, " ").replace(/\s+/g, " ").replace(/;$/, "").trim();

// === CORREGIR TABLA ===
sql = sql.replace(/\bRAW_ORDERS\b/gi, `"${tableName.toUpperCase()}"`);

// === CORREGIR COLUMNAS (solo estas, no literales) ===
// 1ï¸âƒ£ Guardamos los literales entre comillas simples para no tocarlos
let literals = [];
sql = sql.replace(/'([^']*)'/g, (m) => {
  literals.push(m);
  return `__LIT${literals.length - 1}__`;
});

// 2ï¸âƒ£ Reemplazamos columnas solo si son nombres de columna vÃ¡lidos
for (const col of columns) {
  const regex = new RegExp(`\\b${col}\\b`, "gi");
  sql = sql.replace(regex, `"${col.toUpperCase()}"`);
}

// 3ï¸âƒ£ Restauramos literales
sql = sql.replace(/__LIT(\d+)__/g, (_, i) => literals[+i]);

// === CORREGIR errores comunes de HAVING o subconsultas ===
sql = sql.replace(/\)\s*\(\s*SELECT/gi, ") = (SELECT");
sql = sql.replace(/\/\s*9\s*\(\s*SELECT/gi, "/9 = (SELECT");

// === NORMALIZAR ESPACIOS FINALES ===
sql = sql.replace(/\s+/g, " ").trim();

// === RETORNO ===
return [{ cleaned_sql: sql }];
